mapper.insert:

INSERT INTO freeboard (
	  fb_title
    , fb_writer
    , fb_content
    )
VALUES (
	  #{fb_title}
	, #{fb_writer}
	, #{fb_content}
);

mapper.update:
UPDATE freeboard 
SET
	  fb_writer=#{fb_writer}
	, fb_title=#{fb_title}
 	, fb_content=#{fb_content} 
 	, fb_createday=NOW()
WHERE 
	  fb_seq = #{fb_seq}
